import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import {fetchUser} from '../services/api';
import '../styles/Header.css';



//component makes this a class based component with helper functions within it
//anker tag used to go to a completely different page unasscociated with the app
class Header extends Component {
  renderContent() {
    const authentication = fetchUser();
    switch (authentication) {
      case null:
        return;
      case false:
        return <li><a href="/auth/google">Login with Google</a></li>;

      default:
        return <a href="/api/logout" className="right-text">Logout</a>;
    }
  }

  render() {
    return (
      <nav>
        <div className="header">
          <Link 
            to={this.props.auth ? '/home' : '/'}
            className="left"
          >
            Emaily
          </Link>
          <ul className="right">
            {this.renderContent()}
          </ul>
        </div>
      </nav>
    );
  }
}

export default Header;
